import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Search, ZoomIn, ZoomOut, RotateCcw } from "lucide-react";
import OrgChartComponent from "@/components/orgchart/org-chart";
import EmployeePanel from "@/components/orgchart/employee-panel";
import type { Employee } from "@shared/schema";

export default function OrgChart() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedEmployeeId, setSelectedEmployeeId] = useState<string | null>(null);
  const [zoomLevel, setZoomLevel] = useState(100);

  const { data: employees, isLoading } = useQuery<Employee[]>({
    queryKey: ['/api/employees']
  });

  const handleEmployeeSelect = (employeeId: string) => {
    setSelectedEmployeeId(employeeId);
  };

  const handleClosePanel = () => {
    setSelectedEmployeeId(null);
  };

  const handleZoomIn = () => {
    setZoomLevel(prev => Math.min(prev + 25, 200));
  };

  const handleZoomOut = () => {
    setZoomLevel(prev => Math.max(prev - 25, 50));
  };

  const handleFitToScreen = () => {
    setZoomLevel(100);
  };

  const handleClearSearch = () => {
    setSearchTerm("");
  };

  if (isLoading) {
    return (
      <div className="h-full flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
            <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin"></div>
          </div>
          <h3 className="text-lg font-medium mb-2">조직도 로딩 중...</h3>
          <p className="text-sm text-muted-foreground">직원 데이터를 불러오고 있습니다.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex" data-testid="orgchart-page">
      <div className="flex-1 relative">
        {/* Controls */}
        <div className="absolute top-4 left-4 z-10 bg-card border border-border rounded-lg p-3 shadow-lg">
          <div className="flex items-center space-x-2 mb-3">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                type="text"
                placeholder="직원 검색..."
                className="pl-10 w-64"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                data-testid="input-employee-search"
              />
            </div>
            <Button 
              variant="outline" 
              size="sm" 
              onClick={handleClearSearch}
              data-testid="button-clear-search"
            >
              초기화
            </Button>
          </div>
          <div className="flex items-center space-x-2">
            <Button 
              variant="outline" 
              size="sm" 
              onClick={handleZoomOut}
              data-testid="button-zoom-out"
            >
              <ZoomOut className="w-4 h-4" />
            </Button>
            <span className="text-sm text-muted-foreground min-w-[60px] text-center" data-testid="text-zoom-level">
              {zoomLevel}%
            </span>
            <Button 
              variant="outline" 
              size="sm" 
              onClick={handleZoomIn}
              data-testid="button-zoom-in"
            >
              <ZoomIn className="w-4 h-4" />
            </Button>
            <Button 
              variant="secondary" 
              size="sm" 
              onClick={handleFitToScreen}
              data-testid="button-fit-screen"
            >
              <RotateCcw className="w-4 h-4 mr-1" />
              화면 맞춤
            </Button>
          </div>
        </div>

        {/* Skill Legend */}
        <div className="absolute top-4 right-4 z-10 bg-card border border-border rounded-lg p-3 shadow-lg">
          <h4 className="text-sm font-medium mb-2">능력치 범례</h4>
          <div className="space-y-1 text-xs">
            <div className="flex items-center">
              <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
              <span>우수 (80+)</span>
            </div>
            <div className="flex items-center">
              <div className="w-3 h-3 bg-yellow-500 rounded-full mr-2"></div>
              <span>보통 (60-79)</span>
            </div>
            <div className="flex items-center">
              <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
              <span>개선필요 (40-59)</span>
            </div>
            <div className="flex items-center">
              <div className="w-3 h-3 bg-gray-400 rounded-full mr-2"></div>
              <span>미달 (40 미만)</span>
            </div>
          </div>
        </div>

        {/* Org Chart */}
        <OrgChartComponent
          employees={employees || []}
          searchTerm={searchTerm}
          zoomLevel={zoomLevel}
          onEmployeeSelect={handleEmployeeSelect}
        />
      </div>

      {/* Employee Detail Panel */}
      {selectedEmployeeId && (
        <EmployeePanel
          employeeId={selectedEmployeeId}
          onClose={handleClosePanel}
        />
      )}
    </div>
  );
}

client/src/pages/reports.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Download, FileText, BarChart3, PieChart, Users } from "lucide-react";
import SkillRadarChart from "@/components/charts/radar-chart";
import { PieChart as RechartsPieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from "recharts";

interface DepartmentSkills {
  department: string;
  employeeCount: number;
  averageSkills: {
    overall: number;
    experience: number;
    certification: number;
    language: number;
    training: number;
    technical: number;
    softSkill: number;
  };
}

interface DepartmentRatio {
  department: string;
  count: number;
  percentage: number;
}

interface DepartmentRatiosResponse {
  totalEmployees: number;
  departments: DepartmentRatio[];
}

export default function Reports() {
  const [reportType, setReportType] = useState("");
  const [period, setPeriod] = useState("");
  const [department, setDepartment] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  // Define colors for pie chart
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D'];

  const { data: departmentSkills, isLoading } = useQuery<DepartmentSkills[]>({
    queryKey: ['/api/dashboard/department-skills']
  });

  const { data: departmentRatios, isLoading: isLoadingRatios } = useQuery<DepartmentRatiosResponse>({
    queryKey: ['/api/dashboard/department-ratios']
  });

  if (isLoading || isLoadingRatios) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-muted rounded w-1/4"></div>
          <div className="h-20 bg-muted rounded"></div>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-64 bg-muted rounded-lg"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6" data-testid="reports-page">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">보고서 및 분석</h1>
          <p className="text-muted-foreground">교육 및 능력치 현황 분석</p>
        </div>
        <div className="flex space-x-2">
          <Button variant="outline" data-testid="button-generate-report">
            <FileText className="w-4 h-4 mr-2" />
            보고서 생성
          </Button>
          <Button data-testid="button-export-data">
            <Download className="w-4 h-4 mr-2" />
            데이터 내보내기
          </Button>
        </div>
      </div>

      {/* Report Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <Select value={reportType} onValueChange={setReportType}>
              <SelectTrigger data-testid="select-report-type">
                <SelectValue placeholder="보고서 유형" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="department">부서별 분석</SelectItem>
                <SelectItem value="individual">개인별 분석</SelectItem>
                <SelectItem value="training">교육별 분석</SelectItem>
                <SelectItem value="skill">능력치 분석</SelectItem>
              </SelectContent>
            </Select>

            <Select value={period} onValueChange={setPeriod}>
              <SelectTrigger data-testid="select-period">
                <SelectValue placeholder="기간 선택" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="month">이번 달</SelectItem>
                <SelectItem value="quarter">이번 분기</SelectItem>
                <SelectItem value="year">올해</SelectItem>
                <SelectItem value="custom">사용자 지정</SelectItem>
              </SelectContent>
            </Select>

            <Select value={department} onValueChange={setDepartment}>
              <SelectTrigger data-testid="select-department">
                <SelectValue placeholder="부서 선택" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">전체 부서</SelectItem>
                <SelectItem value="hr">인사팀</SelectItem>
                <SelectItem value="it">IT팀</SelectItem>
                <SelectItem value="sales">영업팀</SelectItem>
                <SelectItem value="marketing">마케팅팀</SelectItem>
              </SelectContent>
            </Select>

            <Input
              type="date"
              placeholder="시작일"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              data-testid="input-start-date"
            />

            <Input
              type="date"
              placeholder="종료일"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              data-testid="input-end-date"
            />
          </div>
        </CardContent>
      </Card>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Department Skills Distribution */}
        <Card data-testid="chart-skill-distribution">
          <CardHeader>
            <CardTitle className="flex items-center">
              <BarChart3 className="w-5 h-5 mr-2" />
              부서별 능력 분포도
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-64">
              {departmentSkills && (
                <SkillRadarChart 
                  data={departmentSkills} 
                  dataKey="averageSkills"
                  nameKey="department"
                />
              )}
            </div>
          </CardContent>
        </Card>

        {/* Department Ratios Chart */}
        <Card data-testid="chart-department-ratios">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Users className="w-5 h-5 mr-2" />
              부서별 인원 비율
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-64">
              {departmentRatios && departmentRatios.departments.length > 0 ? (
                <ResponsiveContainer width="100%" height="100%">
                  <RechartsPieChart>
                    <Pie
                      data={departmentRatios.departments}
                      cx="50%"
                      cy="50%"
                      labelLine={false}
                      label={({ name, percent }) => `${name} (${(percent * 100).toFixed(1)}%)`}
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="count"
                      nameKey="department"
                    >
                      {departmentRatios.departments.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip 
                      formatter={(value: number, name: string) => [
                        `${value}명 (${departmentRatios.departments.find(d => d.department === name)?.percentage || 0}%)`,
                        '인원수'
                      ]}
                      labelFormatter={(label: string) => `부서: ${label}`}
                    />
                    <Legend />
                  </RechartsPieChart>
                </ResponsiveContainer>
              ) : departmentRatios && departmentRatios.departments.length === 0 ? (
                <div className="h-64 flex items-center justify-center text-muted-foreground">
                  <div className="text-center">
                    <Users className="w-12 h-12 mx-auto mb-4 opacity-50" />
                    <p>표시할 부서 데이터가 없습니다</p>
                    <p className="text-sm">직원이 등록되면 부서별 비율이 표시됩니다</p>
                  </div>
                </div>
              ) : (
                <div className="h-64 flex items-center justify-center text-muted-foreground">
                  <div className="text-center">
                    <Users className="w-12 h-12 mx-auto mb-4 opacity-50" />
                    <p>부서별 비율 데이터 로딩 중...</p>
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Certification Overview - Placeholder */}
        <Card data-testid="chart-certification-overview">
          <CardHeader>
            <CardTitle>자격증 보유 현황</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-64 flex items-center justify-center text-muted-foreground">
              <div className="text-center">
                <BarChart3 className="w-12 h-12 mx-auto mb-4 opacity-50" />
                <p>자격증 현황 분석 중...</p>
                <p className="text-sm">데이터 집계 중입니다</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Language Proficiency - Placeholder */}
        <Card data-testid="chart-language-proficiency">
          <CardHeader>
            <CardTitle>어학능력 수준별 분포</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-64 flex items-center justify-center text-muted-foreground">
              <div className="text-center">
                <PieChart className="w-12 h-12 mx-auto mb-4 opacity-50" />
                <p>어학능력 분석 중...</p>
                <p className="text-sm">언어별 분포 계산 중입니다</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Detailed Analytics Table */}
      <Card data-testid="analytics-table">
        <CardHeader>
          <CardTitle>상세 분석 데이터</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-border">
                  <th className="text-left py-3 px-6 font-medium text-muted-foreground">부서</th>
                  <th className="text-left py-3 px-6 font-medium text-muted-foreground">직원수</th>
                  <th className="text-left py-3 px-6 font-medium text-muted-foreground">평균 능력치</th>
                  <th className="text-left py-3 px-6 font-medium text-muted-foreground">교육 완료율</th>
                  <th className="text-left py-3 px-6 font-medium text-muted-foreground">자격증 보유율</th>
                  <th className="text-left py-3 px-6 font-medium text-muted-foreground">추천사항</th>
                </tr>
              </thead>
              <tbody>
                {departmentSkills?.map((dept) => (
                  <tr key={dept.department} className="border-b border-border">
                    <td className="py-4 px-6 font-medium">{dept.department}</td>
                    <td className="py-4 px-6">{dept.employeeCount}명</td>
                    <td className="py-4 px-6">
                      <div className="flex items-center">
                        <span className={`font-medium ${
                          dept.averageSkills.overall >= 80 ? 'text-green-600' :
                          dept.averageSkills.overall >= 60 ? 'text-yellow-600' :
                          'text-red-600'
                        }`}>
                          {dept.averageSkills.overall.toFixed(1)}
                        </span>
                        <div className={`w-2 h-2 rounded-full ml-2 ${
                          dept.averageSkills.overall >= 80 ? 'bg-green-500' :
                          dept.averageSkills.overall >= 60 ? 'bg-yellow-500' :
                          'bg-red-500'
                        }`}></div>
                      </div>
                    </td>
                    <td className="py-4 px-6">-</td>
                    <td className="py-4 px-6">-</td>
                    <td className="py-4 px-6">
                      <span className={`px-2 py-1 rounded-full text-sm ${
                        dept.averageSkills.overall >= 80 
                          ? 'bg-green-100 text-green-800' 
                          : dept.averageSkills.overall >= 60
                          ? 'bg-yellow-100 text-yellow-800'
                          : 'bg-red-100 text-red-800'
                      }`}>
                        {dept.averageSkills.overall >= 80 ? '우수' : 
                         dept.averageSkills.overall >= 60 ? '보통' : '개선필요'}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/training.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Plus, Upload, BookOpen, Clock, Users, Calendar } from "lucide-react";
import TrainingTable from "@/components/training/training-table";
import TrainingFormDialog from "@/components/training/training-form-dialog";
import TrainingUploadDialog from "@/components/training/training-upload-dialog";
import type { TrainingHistory } from "@shared/schema";

interface TrainingStats {
  total: number;
  completed: number;
  ongoing: number;
  scheduled: number;
}

export default function Training() {
  const { data: trainings, isLoading } = useQuery<TrainingHistory[]>({
    queryKey: ['/api/training']
  });

  // Calculate training stats
  const stats: TrainingStats = {
    total: trainings?.length || 0,
    completed: trainings?.filter(t => t.status === 'completed').length || 0,
    ongoing: trainings?.filter(t => t.status === 'ongoing').length || 0,
    scheduled: trainings?.filter(t => t.status === 'planned').length || 0
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-muted rounded w-1/4"></div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-24 bg-muted rounded-lg"></div>
            ))}
          </div>
          <div className="h-96 bg-muted rounded-lg"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6" data-testid="training-page">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">교육 관리</h1>
          <p className="text-muted-foreground">교육 과정 및 이수 현황 관리</p>
        </div>
        <div className="flex space-x-2">
          <TrainingUploadDialog>
            <Button variant="outline" data-testid="button-import-training">
              <Upload className="w-4 h-4 mr-2" />
              엑셀 가져오기
            </Button>
          </TrainingUploadDialog>
          <TrainingFormDialog>
            <Button data-testid="button-add-training">
              <Plus className="w-4 h-4 mr-2" />
              새 교육 추가
            </Button>
          </TrainingFormDialog>
        </div>
      </div>

      {/* Training Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card data-testid="stats-total-training">
          <CardContent className="p-4 text-center">
            <div className="flex items-center justify-center mb-2">
              <BookOpen className="w-8 h-8 text-primary" />
            </div>
            <p className="text-2xl font-bold text-primary">{stats.total}</p>
            <p className="text-sm text-muted-foreground">총 교육과정</p>
          </CardContent>
        </Card>

        <Card data-testid="stats-completed-training">
          <CardContent className="p-4 text-center">
            <div className="flex items-center justify-center mb-2">
              <Users className="w-8 h-8 text-green-600" />
            </div>
            <p className="text-2xl font-bold text-green-600">{stats.completed}</p>
            <p className="text-sm text-muted-foreground">완료된 교육</p>
          </CardContent>
        </Card>

        <Card data-testid="stats-ongoing-training">
          <CardContent className="p-4 text-center">
            <div className="flex items-center justify-center mb-2">
              <Clock className="w-8 h-8 text-yellow-600" />
            </div>
            <p className="text-2xl font-bold text-yellow-600">{stats.ongoing}</p>
            <p className="text-sm text-muted-foreground">진행중인 교육</p>
          </CardContent>
        </Card>

        <Card data-testid="stats-scheduled-training">
          <CardContent className="p-4 text-center">
            <div className="flex items-center justify-center mb-2">
              <Calendar className="w-8 h-8 text-blue-600" />
            </div>
            <p className="text-2xl font-bold text-blue-600">{stats.scheduled}</p>
            <p className="text-sm text-muted-foreground">예정된 교육</p>
          </CardContent>
        </Card>
      </div>

      {/* Training Table */}
      <Card>
        <CardHeader>
          <CardTitle>교육 과정 목록</CardTitle>
        </CardHeader>
        <CardContent>
          {trainings && trainings.length > 0 ? (
            <TrainingTable trainings={trainings} />
          ) : (
            <div className="text-center py-12 text-muted-foreground">
              <BookOpen className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <h3 className="text-lg font-medium mb-2">등록된 교육과정이 없습니다</h3>
              <p className="text-sm mb-4">새로운 교육과정을 추가하여 시작하세요.</p>
              <TrainingFormDialog>
                <Button>
                  <Plus className="w-4 h-4 mr-2" />
                  교육과정 추가
                </Button>
              </TrainingFormDialog>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/types/employee.ts.content:
/**
 * Extended types for the Ashimori Education Management System
 * These types extend the base schema types with additional UI-specific properties
 */

import type { 
  Employee, 
  TrainingHistory, 
  Certification, 
  Language, 
  Skill,
  SkillCalculation 
} from "@shared/schema";

// Extended employee type with calculated fields
export interface ExtendedEmployee extends Employee {
  yearsOfExperience?: number;
  skillLevel?: 'high' | 'medium' | 'low' | 'none';
  overallScore?: number;
  recentTrainingCount?: number;
  activeCertificationCount?: number;
}

// Full employee profile with all related data
export interface EmployeeProfile {
  employee: Employee;
  trainingHistory: TrainingHistory[];
  certifications: Certification[];
  languages: Language[];
  skills: Skill[];
  skillCalculation?: SkillCalculation;
}

// Department statistics for analytics
export interface DepartmentStats {
  department: string;
  employeeCount: number;
  averageSkills: {
    overall: number;
    experience: number;
    certification: number;
    language: number;
    training: number;
    technical: number;
    softSkill: number;
  };
  topPerformers: Array<{
    id: string;
    name: string;
    score: number;
  }>;
}

// Organization chart node data
export interface OrgChartNode {
  id: string;
  name: string;
  position: string;
  department: string;
  managerId?: string;
  children?: OrgChartNode[];
  skillIndicators: {
    experience: number;
    certification: number;
    language: number;
    training: number;
  };
  overallSkillLevel: 'high' | 'medium' | 'low' | 'none';
}

// Training statistics
export interface TrainingStats {
  total: number;
  completed: number;
  ongoing: number;
  planned: number;
  cancelled: number;
  totalHours: number;
  averageCompletionRate: number;
}

// Dashboard KPI data
export interface DashboardKPIs {
  totalEmployees: number;
  completionRate: number;
  trainingHours: number;
  certifiedEmployees: number;
  averageSkillScore: number;
  topDepartment: string;
}

// Top performers data
export interface TopPerformer {
  id: string;
  name: string;
  position: string;
  department: string;
  score: number;
  rank: number;
  avatar?: string;
}

// Skill comparison data for charts
export interface SkillComparison {
  employee: {
    id: string;
    name: string;
    department: string;
  };
  skills: {
    experience: number;
    certification: number;
    language: number;
    training: number;
    technical: number;
    softSkill: number;
  };
  overallScore: number;
}

// Training course with enrollment data
export interface TrainingCourse extends TrainingHistory {
  enrolledCount: number;
  completedCount: number;
  completionRate: number;
  averageScore?: number;
  participants: Array<{
    employeeId: string;
    employeeName: string;
    department: string;
    status: string;
    completionDate?: string;
    score?: number;
  }>;
}

// Filter options for UI components
export interface EmployeeFilters {
  search: string;
  department: string;
  skillLevel: string;
  sortBy: 'name' | 'department' | 'score' | 'hireDate';
  sortOrder: 'asc' | 'desc';
}

export interface TrainingFilters {
  search: string;
  status: string;
  type: string;
  category: string;
  dateRange: {
    start?: string;
    end?: string;
  };
}

// Report generation options
export interface ReportOptions {
  type: 'department' | 'individual' | 'training' | 'skill';
  period: 'month' | 'quarter' | 'year' | 'custom';
  department?: string;
  dateRange?: {
    start: string;
    end: string;
  };
  format: 'pdf' | 'excel' | 'csv';
  includeCharts: boolean;
}

// Bulk import/export data structures
export interface BulkImportData {
  employees?: Partial<Employee>[];
  trainings?: Partial<TrainingHistory>[];
  certifications?: Partial<Certification>[];
  languages?: Partial<Language>[];
  skills?: Partial<Skill>[];
